import { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button } from 'react-bootstrap'
// import Navbar from '.components/Navbar'

function Cities(){
    const url = "https://testapi.photodino.de/cities/"
    const [data, setData] = useState([]);
    const [name, setName] = useState("");
    const [code, setCode] = useState("");
    const [message, setMessage] = useState("");

useEffect(() => { 
    getData();

    }, [])

    const getData = () => {
        fetch(url)
        .then(response => response.json())
        .then(json => {setData(json);}).catch(error =>{console.log('error', error.message)})
    }

    const deleteCities = (id) => {
    const url = "https://testapi.photodino.de/cities/"+id+"/"
    fetch(url, {
        method: 'DELETE',
        body: JSON.stringify(data),
    })
    .then(response => {
        console.log("response", response)
        getData();
        // eslint-disable-next-line eqeqeq
        if(response.ok){
           console.log("success delete")
        } else {
            console.log("error delete")
        }
    }).catch(error =>{
        console.log('erreur', error)

    })
}

const updateCities = (id) => {
    const data = {
        name: name,
        code: code,
    }
    const url = "https://testapi.photodino.de/cities/"+id+"/"
    fetch(url, {
        method: 'PUT',
        body: JSON.stringify(data),
    })
    .then(response => {
        console.log("response", response)
        getData();
        // eslint-disable-next-line eqeqeq
        if(response.ok){
           console.log("success update")
        } else {
            console.log("error update")
        }
    }).catch(error =>{
        console.log('erreur', error)

    })
}
    return (

    <>
    <div class="table-responsive">
        <header/>
        <h1 className="m-3">List of city</h1>
        <table class="table">
            <thead>
            <tr>
                <th>Name</th>
                <th>Locations</th>
                <th>Date and Time</th>
                <th>Operations</th>
                <th>Change City</th>
            </tr>
            </thead>
            <tbody>
                {data.map((item) => {
                    return (
                        <tr key={item.id}>
                            <td>{item.name}</td>
                            <td>{item.locations}</td>
                            <td>{item.time_added}</td>
                            <td><Button onClick={() => deleteCities(item.id)}><span>Delete</span></Button></td>
                            <td><Button onClick={() => updateCities(item.id)}><span>Edit</span></Button></td>
                        </tr>
                        );
                    })}
            </tbody>
        </table>
        <form onSubmit={updateCities}>
                    <input
                    type="text"
                    value={name}
                    placeholder="Name"
                    onChange={(e) => setName(e.target.value)}
                    />
                    <input
                    type="text"
                    value={code}
                    placeholder="Code"
                    onChange={(e) => setCode(e.target.value)}
                    />              
                    <button type="submit">Edit</button>

                    <div className="message">{message ? <p>{message}</p> : null}</div>
                </form>
        {/* <NavBar/> */}
    </div>
    </>
    )
}

export default Cities;
