import { useState } from "react";

function SENDEVENTS ()  {
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [event_date, setEventdate] = useState("");
    const [message, setMessage] = useState("");

    const postEvent = (e) => {
        e.preventDefault();
        const data = {
            name: name,
            description: description,
            event_date: event_date,
        }
        const url = "https://testapi.photodino.de/events/"
        fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        })
        .then(response => {
            console.log("response", response);
            // eslint-disable-next-line eqeqeq
            if(response.ok){
                setName("")
            }
            }).catch(error =>{
                console.log('erreur', error)

            })
        }

        return(
            <div class="container">
                <form onSubmit={postEvent}>
                    <div class="row">
                    <div class="col-25">
                        <label for="fname">First Name</label>
                    </div>
                    <div class="col-75">
                        <input type="text" id="fname" value={name}  placeholder="Name" onChange={(e) => setName(e.target.value)} />
                    </div>
                    </div>
                    <div class="row">
                    <div class="col-25">
                        <label for="lname">Last Name</label>
                    </div>
                    <div class="col-75">
                        <input type="text" id="lname" name="lastname" placeholder="Your last name.." />
                    </div>
                    </div>
                    <div class="row">
                    <div class="col-25">
                        <label for="country">Country</label>
                    </div>
                    <div class="col-75">
                        <select id="country" name="country">
                        <option value="australia">Australia</option>
                        <option value="canada">Canada</option>
                        <option value="usa">USA</option>
                        </select>
                    </div>
                    </div>
                    <div class="row">
                    <div class="col-25">
                        <label for="subject">Subject</label>
                    </div>
                    <div class="col-75">
                        <textarea id="subject" type="text" value={description} placeholder="Description" onChange={(e) => setDescription(e.target.value)} style="height:200px"></textarea>
                    </div>
                    </div>
                    <div class="row">
                    <input type="submit" value="Submit" />
                    </div>
                </form>
            </div>
        );
}

export default SENDEVENTS;
