import { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button, Alert, Breadcrumb, Card } from 'react-bootstrap'

function Cities(){
    const url = "https://testapi.photodino.de/cities/"
    const [data, setData] = useState([]);

useEffect(() => { 
    getData();

    }, [])

    const getData = () => {
        fetch(url)
        .then(response => response.json())
        .then(json => {setData(json);}).catch(error =>{console.log('error', error.message)})
    }

    const postEvent = () => {
        const data = {
            name: 'Benin',
            locations: [],
        }
    const url = "https://testapi.photodino.de/cities/"
    fetch(url, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
    })
    .then(response => {
        console.log("response", response);
        getData();
        // eslint-disable-next-line eqeqeq
        if(response.ok){
                alert("success")
            }
        }).catch(error =>{
            console.log('erreur', error)

        })
    }

    const deleteCities = (id) => {
    const url = "https://testapi.photodino.de/cities/"+id+"/"
    fetch(url, {
        method: 'DELETE',
        body: JSON.stringify(data),
    })
    .then(response => {
        console.log("response", response)
        getData();
        // eslint-disable-next-line eqeqeq
        if(response.ok){
            alert("success")
        } else {
            alert("erreur")
        }
    }).catch(error =>{
        console.log('erreur', error)

    })
}
    return (

    <>
    <div class="table-responsive">
        <header/>
        <h1 className="m-3">List of city</h1>
        <table class="table">
            <thead>
            <tr>
                <th>Name</th>
                <th>Locations</th>
                <th>Date and Time</th>
                <th>Operations</th>
            </tr>
            </thead>
            <tbody>
                {data.map((item) => {
                    return (
                        <tr key={item.id}>
                            <td>{item.name}</td>
                            <td>{item.locations}</td>
                            <td>{item.time_added}</td>
                            <td><Button onClick={() => deleteCities(item.id)}><span>Delete</span></Button></td>
                        </tr>
                        );
                    })}
            </tbody>
        </table>
    </div>

    <button onClick={postEvent}>Submit</button>
    </>
    )
}

export default Cities;
