import { useEffect, useState } from 'react';

function Events(){
    const url = "https://testapi.photodino.de/events/"
    const [data, setData] = useState([]);

    useEffect(() => { 
        fetch(url, {
            header: {

            }
        } )
          .then(response => response.json())
          .then(json => {
                console.log('bdbvbdibd', json)
              setData(json);
            }).catch(error =>{
                console.log('jvcgugvu', error)
            })

      }, [])


      const postEvent = () => {
          const data = {
              name: 'Benin',
              locations: [],
          }
        const url = "https://testapi.photodino.de/events/"
        fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        })
        .then(response => {
            console.log("response", response)
            // eslint-disable-next-line eqeqeq
            if(response.ok){
                  alert("success")
              }
          }).catch(error =>{
              console.log('erreur', error)

          })
      }

      const putEvent = (id) => {
        const data = {
            name: 'Benin',
            locations: [],
        }
      const url = "https://testapi.photodino.de/events/"+id
      fetch(url, {
          method: 'PUT',
          headers: {
              'Content-Type': 'application/json',
          },
          body: JSON.stringify(data),
      })
      .then(response => {
          console.log("response", response)
          // eslint-disable-next-line eqeqeq
          if(response.ok){
                alert("success")
            }
        }).catch(error =>{
            console.log('erreur', error)

        })
    }
      return (
        <div className = "App" style={{justifyContent: 'center', textAlign: 'justify', backgroundColor: 'red', display: 'inline-block'}}>
                    <div>

                        <table>
                            <thead> 
                                <tr>
                                    <th>Name</th>
                                    <th>Locations</th>
                                    <th style={{alignItems: 'right', color: 'white'}}>Added</th>
                                </tr>
                            </thead>

                            <tbody> 
                            {data.map((item) => {
                                return (
                                    <tr key={item.id}>
                                    <td>{item.name}</td>
                                </tr>
                                )
                            })}

                            </tbody>

                        </table>
                    </div>
        <button onClick={postEvent}>Submit</button>
        <di>
        <button onClick={putEvent}>delete</button>
        </di>
    </div>
      )
}

export default Events;
