import { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button } from 'react-bootstrap'
import { NavLink } from 'react-router-dom'
import {FaListAlt, FaCheckSquare, FaPlusSquare, FaTrash} from 'react-icons/fa';
import {  Link, Outlet,useNavigate, useParams } from "react-router-dom";

function Cities(props){
    let navigate = useNavigate();
    let { id } = useParams();
    const url = "https://testapi.photodino.de/cities/"
    const [data, setData] = useState([]);
    const [name, setName] = useState("");
    const [code, setCode] = useState("");
    const [message, setMessage] = useState("");

useEffect(() => { 
    getData();
    }, [])
    const getData = () => {
        fetch(url)
        .then(response => response.json())
        .then(json => {setData(json);}).catch(error =>{console.log('error', error.message)})
    }

    const deleteCities = (id) => {
    const url = "https://testapi.photodino.de/cities/"+id+"/"
    fetch(url, {
        method: 'DELETE',
        body: JSON.stringify(data),
    })
    .then(response => {
        console.log("response", response)
        getData();
        // eslint-disable-next-line eqeqeq
        if(response.ok){
           console.log("success delete")
        } else {
            console.log("error delete")
        }
    }).catch(error =>{
        console.log('erreur', error)

    })
}
    return (

    <>
    <div class="table-responsive">
        <header/>
        <h1 className="m-3">List of city</h1>
        <table class="table">
            <thead>
            <tr>
                <th>Name</th>
                <th>Locations</th>
                <th>Date and Time</th>
                <th>Operations</th>
            </tr>
            </thead>
            <tbody>
                {data.map((item) => {
                    return (
                        <tr key={item.id}>
                            <td>{item.name}</td>
                            <td>{item.locations}</td>
                            <td>{item.time_added}</td>
                            <td><Button onClick={() => deleteCities(item.id)} className="btn btn-outline-dark bg-light"><span><FaTrash /></span></Button></td>
                            <td><Button onClick={() => navigate('/:id')}><span>Edit</span></Button></td>
                        </tr>
                        );
                    })}
            </tbody>
        </table>
            <div class="d-flex p-2 justify-content-center"> 
                <Button onClick={() => navigate('/add-cities')}><span class="d-inline-block p-2 flex-row">Add Cities</span></Button>
            </div>
    </div>
    </>
    )
}

export default Cities;
